scripts.prepare-debug-version:
node -e 'const { execSync } = require("child_process"); const fs = require("fs"); const commitHash = execSync("git rev-parse --short HEAD").toString().trim(); const versionContent = `// This file is auto-generated for debugging purposes.
// It contains the Git commit hash of the last local state when "npm run prepare-debug-version" was run.
function getAppVersion() {
  return "DEBUG_VERSION: ${commitHash}";
}
`; fs.writeFileSync("src/Version.js", versionContent); console.log("src/Version.js updated with debug version info.");'

scripts.prepare-prod-version:
node -e 'const { execSync } = require("child_process"); const fs = require("fs"); const commitHash = execSync("git rev-parse --short HEAD").toString().trim(); const versionContent = `// This file is auto-generated for production deployment.
// It contains the Git commit hash of the committed version.
function getAppVersion() {
  return "PRODUCTION_VERSION: ${commitHash}";
}
`; fs.writeFileSync("src/Version.js", versionContent); console.log("src/Version.js updated with production version info.");'
